356     struct Vector2 {
357         float x, y;
358 
359         explicit Vector2() : x(0.0f), y(0.0f) {}
360 
361         Vector2(float x, float y) : x(x), y(y) {}
362 
368         void reset(float x, float y) {
369             this->x = x;
370             this->y = y;
371         }
372 
373         void reset(const Vector2 &vector2) {
374             this->x = vector2.x;
375             this->y = vector2.y;
376         }
377 
378         Vector2 operator+(const Vector2 &v) const { return {this->x + v.x, this->y + v.y}; }
379 
380         Vector2 operator-(const Vector2 &v) const { return {this->x - v.x, this->y - v.y}; }
381 
382         Vector2 operator*(const Vector2 &v) const { return {this->x * v.x, this->y * v.y}; }
383 
384         Vector2 operator*(float v) const { return {this->x * v, this->y * v}; };
385 
386         Vector2 operator/(const Vector2 &v) const { return {this->x / v.x, this->y / v.y}; }
387 
388         Vector2 operator/(float v) const { return (v != 0) ? Vector2(this->x / v, this->y / v) : *this; }
389 
390         void operator+=(const Vector2 &v) {
391             this->x += v.x;
392             this->y += v.y;
393         }
394 
395         void operator-=(const Vector2 &v) {
396             this->x -= v.x;
397             this->y -= v.y;
398         }
399 
400         void operator*=(const Vector2 &v) {
401             this->x *= v.x;
402             this->y *= v.y;
403         }
404 
405         void operator*=(float v) {
406             this->x *= v;
407             this->y *= v;
408         }
409 
410         void operator/=(const Vector2 &v) {
411             this->x /= v.x;
412             this->y /= v.y;
413         }
414 
415         void operator/=(float v) {
416             if (v == 0) return;
417             this->x /= v;
418             this->y /= v;
419         }
420 
421         bool operator==(const Vector2 &v) const {
422             return ((this->x != v.x) ? false : ((this->y != v.y) ? false : true));
423         }
424 
425         bool operator!=(const Vector2 &v) const {
426             return ((this->x != v.x) ? true : ((this->y != v.y) ? true : false));
427         }
428 
429         bool operator>(const Vector2 &v) const {
430             return (this->x > v.x && this->y > v.y);
431         }
432 
433         bool operator>=(const Vector2 &v) const {
434             return (this->x >= v.x && this->y >= v.y);
435         }
436 
437         bool operator<(const Vector2 &v) const {
438             return (this->x < v.x && this->y < v.y);
439         }
440 
441         bool operator<=(const Vector2 &v) const {
442             return (this->x <= v.x && this->y <= v.y);
443         }
444 
452         [[nodiscard]] bool isEqual(const Vector2 &v, const float EPISON = 1e-6f) const {
453             return ((this->x >= v.x - EPISON && this->x <= v.x + EPISON) &&
454                     (this->y >= v.y - EPISON && this->y <= v.y + EPISON));
455         }
456     };
