464     struct Size {
465         float width, height;
466 
467         explicit Size() : width(0.0f), height(0.0f) {}
468 
469         Size(float width, float height) : width(width), height(height) {}
470 
476         void reset(float width, float height) {
477             this->width = width;
478             this->height = height;
479         }
480 
481         void reset(const Size &size) {
482             this->width = size.width;
483             this->height = size.height;
484         }
485 
486         Size operator+(const Size &s) const { return {this->width + s.width, this->height + s.height}; }
487 
488         Size operator-(const Size &s) const { return {this->width - s.width, this->height - s.height}; }
489 
490         Size operator*(const Size &s) const { return {this->width * s.width, this->height * s.height}; }
491 
492         Size operator*(float v) const { return {this->width * v, this->height * v}; }
493 
494         Size operator/(const Size &s) const { return {this->width / s.width, this->height / s.height}; }
495 
496         Size operator/(float v) const { return (v == 0) ? *this : Size(this->width / v, this->height / v); }
497 
498         void operator+=(const Size &s) {
499             this->width += s.width;
500             this->height += s.height;
501         }
502 
503         void operator-=(const Size &s) {
504             this->width -= s.width;
505             this->height -= s.height;
506         }
507 
508         void operator*=(const Size &s) {
509             this->width *= s.width;
510             this->height *= s.height;
511         }
512 
513         void operator*=(float v) {
514             this->width *= v;
515             this->height *= v;
516         }
517 
518         void operator/=(const Size &s) {
519             this->width /= s.width;
520             this->height /= s.height;
521         }
522 
523         void operator/=(float v) {
524             if (v == 0) return;
525             this->width /= v;
526             this->height /= v;
527         }
528 
529         bool operator==(const Size &s) const {
530             return (this->width != s.width) ? false : ((this->height != this->height) ? false : true);
531         }
532 
533         bool operator!=(const Size &s) const {
534             return (this->width != s.width) ? true : ((this->height != this->height) ? true : false);
535         }
536 
537         bool operator>(const Size &s) const {
538             return (this->width > s.width && this->height > s.height);
539         }
540 
541         bool operator>=(const Size &s) const {
542             return (this->width >= s.width && this->height >= s.height);
543         }
544 
545         bool operator<(const Size &s) const {
546             return (this->width < s.width && this->height < s.height);
547         }
548 
549         bool operator<=(const Size &s) const {
550             return (this->width <= s.width && this->height <= s.height);
551         }
552     };
