560     struct GeometryF {
561         Vector2 pos;
562         Size size;
563 
564         explicit GeometryF(float x, float y, float w, float h)
565                 : pos(x, y), size(w, h) {}
566 
567         explicit GeometryF(const Vector2 &pos, const Size &size)
568                 : pos(pos), size(size) {}
569 
570         explicit GeometryF(Vector2 &&pos, Size &&size)
571                 : pos(pos), size(size) {}
572 
573         explicit GeometryF() : pos(0, 0), size(0, 0) {}
574 
575         void reset(const Vector2 &pos, const Size &size) {
576             this->pos.x = pos.x;
577             this->pos.y = pos.y;
578             this->size.width = size.width;
579             this->size.height = size.height;
580         }
581 
582         void reset(float x, float y, float width, float height) {
583             this->pos.x = x;
584             this->pos.y = y;
585             this->size.width = width;
586             this->size.height = height;
587         }
588 
589         void reset(const GeometryF& geometry) {
590             this->pos.x = geometry.pos.x;
591             this->pos.y = geometry.pos.y;
592             this->size.width = geometry.size.width;
593             this->size.height = geometry.size.height;
594         }
595 
596         void resetPos(const Vector2 &pos) {
597             this->pos.x = pos.x;
598             this->pos.y = pos.y;
599         }
600 
601         void resetSize(const Size &size) {
602             this->size.width = size.width;
603             this->size.height = size.height;
604         }
605     };
