614     template<typename T>
615     struct Matrix2D {
616     private:
618         std::vector<T> _datas;
620         uint32_t _row;
622         uint32_t _col;
624         std::function<void(T &)> _deleter;
625     public:
626         using iterator = typename std::vector<T>::iterator;
627         using constIterator = typename std::vector<T>::const_iterator;
628 
629         struct Position {
630             uint32_t row;
631             uint32_t col;
632 
633             Position() : row(0), col(0) {}
634 
635             Position(uint32_t row, uint32_t col) : row(row), col(col) {}
636 
637             bool isValid() const { return row && col; }
638 
639             bool operator==(const Position &p) const { return (row == p.row && col == p.col); }
640 
641             bool operator!=(const Position &p) const { return (row != p.row || col != p.col); }
642 
643             bool operator>(const Position &p) const { return (row >= p.row && col > p.col); }
644 
645             bool operator<(const Position &p) const { return (row <= p.row && col < p.col); }
646 
647             bool operator>=(const Position &p) const { return (row >= p.row && col >= p.col); }
648 
649             bool operator<=(const Position &p) const { return (row <= p.row && col <= p.col); }
650         };
651 
655         explicit Matrix2D() : _row(0), _col(0), _datas() {}
656 
663         Matrix2D(uint32_t row, uint32_t col, const std::function<void(T &)> &deleter = {});
664 
672         Matrix2D(uint32_t row, uint32_t col, const T &value, const std::function<void(T &)> &deleter = {});
673 
678         Matrix2D(const Matrix2D<T> &matrix);
679 
680         ~Matrix2D();
681 
688         void setDeleter(const std::function<void(T &)> &function);
689 
694         void fill(const T &value);
695 
702         bool fillN(const Matrix2D::Position &start, const Matrix2D::Position &end, const T &value);
703 
709         void resize(uint32_t line, uint32_t col);
710 
718         T &at(uint32_t row, uint32_t col);
719 
728         const T &get(uint32_t row, uint32_t col);
729 
734         [[nodiscard]] uint32_t rows() const;
735 
740         [[nodiscard]] uint32_t cols() const;
741 
742         Matrix2D operator+(const Matrix2D<T> &other) const;
743 
744         Matrix2D operator-(const Matrix2D<T> &other) const;
745 
754         Matrix2D operator*(const Matrix2D<T> &other) const;
755 
756         bool operator==(const Matrix2D<T> &other) const;
757 
758         bool operator!=(const Matrix2D<T> &other) const;
759 
760         T &operator[](uint32_t index);
761 
762         T &operator()(uint32_t row, uint32_t col);
763 
764         iterator begin() { return _datas.begin(); }
765 
766         iterator end() { return _datas.end(); }
767 
768         constIterator begin() const { return _datas.begin(); }
769 
770         constIterator end() const { return _datas.end(); }
771 
779         void add(T &value, const std::function<void(T &, T &)> &function = {});
780 
788         void add(T &&value, const std::function<void(T &, T &)> &function = {});
789 
797         void minus(T &value, const std::function<void(T &, T &)> &function = {});
798 
806         void minus(T &&value, const std::function<void(T &, T &)> &function = {});
807 
815         void times(T &value, const std::function<void(T &, T &)> &function = {});
816 
824         void times(T &&value, const std::function<void(T &, T &)> &function = {});
825 
832         void times(const Matrix2D<T> &other, const std::function<void(T &, const T &)> &function = {});
833 
842         void multiply(const Matrix2D<T> &other);
843 
850         void transpose();
851 
861         void reverse(bool reverse_row = true, bool reverse_col = false);
862 
869         void rotate(bool turn_right = true);
870 
877         Matrix2D splitRows(uint32_t start_row, uint32_t end_row);
878 
885         Matrix2D splitCols(uint32_t start_col, uint32_t end_col);
886 
899         Matrix2D split(uint32_t rows, uint32_t cols, const Position &start_pos);
900 
910         Matrix2D split(Matrix2D::Position start_pos, Matrix2D::Position end_pos);
911 
918         Matrix2D<T> inverse();
919     };
920 
921     template<typename T>
922     void Matrix2D<T>::setDeleter(const std::function<void(T &)> &function) {
923         _deleter = function;
924     }
925 
926     template<typename T>
927     Matrix2D<T>::Matrix2D(uint32_t row, uint32_t col, const std::function<void(T &)> &deleter)
928             : _row(row), _col(col), _deleter(deleter) {
929         _datas.resize(_row * _col);
930     }
931 
932     template<typename T>
933     Matrix2D<T>::Matrix2D(uint32_t row, uint32_t col, const T &value, const std::function<void(T &)> &deleter)
934             : _row(row), _col(col), _deleter(deleter) {
935         _datas.resize(_row * _col);
936         fill(value);
937     }
938 
939     template<typename T>
940     Matrix2D<T>::Matrix2D(const Matrix2D<T> &matrix)
941             : _row(matrix._row), _col(matrix._col), _deleter(matrix._deleter) {
942         _datas = matrix._datas;
943     }
944 
945     template<typename T>
946     Matrix2D<T>::~Matrix2D() {
947         if (_deleter) {
948             for (auto &_d: _datas) {
949                 _deleter(_d);
950             }
951         }
952     }
953 
954     template<typename T>
955     void Matrix2D<T>::fill(const T &value) {
956         std::fill(_datas.begin(), _datas.end(), value);
957     }
958 
959     template<typename T>
960     bool Matrix2D<T>::fillN(const Matrix2D::Position &start, const Matrix2D::Position &end, const T &value) {
961         auto st = start.row * _col + start.col, ed = end.row * _col + end.col;
962         if (ed >= _datas.size()) {
963             SDL_Log("[ERROR] One of the specified position is not valid!");
964             return false;
965         }
966         std::fill(_datas.begin() + st, _datas.begin() + ed, value);
967         return true;
968     }
969 
970     template<typename T>
971     void Matrix2D<T>::resize(uint32_t line, uint32_t col) {
972         if (_row * _col != line * col) _datas.resize(line * col);
973         _row = line;
974         _col = col;
975     }
976 
977     template<typename T>
978     T &Matrix2D<T>::at(uint32_t row, uint32_t col) {
979         auto idx = row * _col + col;
980         if (idx >= _datas.size()) throw std::out_of_range("[FATAL] The specified position is out of range!");
981         return _datas.at(idx);
982     }
983 
984     template<typename T>
985     const T &Matrix2D<T>::get(uint32_t row, uint32_t col) {
986         auto idx = row * _col + col;
987         if (idx >= _datas.size()) throw std::out_of_range("[FATAL] The specified position is out of range!");
988         return _datas.at(idx);
989     }
