1001     template<typename T>
1002     Matrix2D<T> Matrix2D<T>::operator+(const Matrix2D<T> &other) const {
1003         if ((_row == other._row) && (_col == other._col)) {
1004             Matrix2D<T> _ret(_row, _col);
1005             for (size_t _idx = 0; _idx < _datas.size(); ++_idx) {
1006                 _ret[_idx] = _datas[_idx] + other._datas[_idx];
1007             }
1008             return _ret;
1009         } else {
1010             SDL_Log("[ERROR] Matrix dimensions mismatch!\nOriginal: (%d, %d), Specified: (%d, %d)",
1011                     _row, _col, other._row, other._col);
1012             return Matrix2D<T>();
1013         }
1014     }
1015 
1016     template<typename T>
1017     Matrix2D<T> Matrix2D<T>::operator-(const Matrix2D<T> &other) const {
1018         if ((_row == other._row) && (_col == other._col)) {
1019             Matrix2D<T> _ret(_row, _col);
1020             for (size_t _idx = 0; _idx < _datas.size(); ++_idx) {
1021                 _ret[_idx] = _datas[_idx] - other._datas[_idx];
1022             }
1023             return _ret;
1024         } else {
1025             SDL_Log("[ERROR] Matrix dimensions mismatch!\nOriginal: (%d, %d), Specified: (%d, %d)",
1026                     _row, _col, other._row, other._col);
1027             return Matrix2D<T>();
1028         }
1029     }
1030 
1031     template<typename T>
1032     Matrix2D<T> Matrix2D<T>::operator*(const Matrix2D<T> &other) const {
1033         if (_col != other._row) {
1034             SDL_Log("[ERROR] Matrix dimensions incompatible for multiplication!");
1035             return Matrix2D<T>();
1036         }
1037         if constexpr (!std::is_integral_v<std::decay_t<T>> &&
1038                       !std::is_floating_point_v<std::decay_t<T>>) {
1039             static_assert(!std::is_integral_v<std::decay_t<T>> &&
1040                           !std::is_floating_point_v<std::decay_t<T>>,
1041                           "[FATAL] Can't support the current data type!");
1042         }
1043         Matrix2D<T> result(_row, other._col, other._deleter);
1044         for (size_t i = 0; i < _row; ++i) {
1045             for (size_t k = 0; k < _col; ++k) {
1046                 const T &a_ik = _datas[i * _col + k];
1047                 for (size_t j = 0; j < other._col; ++j) {
1048                     result[i * other._col + j] += a_ik * other._datas[k * other._col + j];
1049                 }
1050             }
1051         }
1052         return result;
1053     }
1054 
1055     template<typename T>
1056     bool Matrix2D<T>::operator==(const Matrix2D<T> &other) const {
1057         if (_row != other._row || _col != other._col) return false;
1058         for (size_t i = 0; i < _datas.size(); ++i) {
1059             if (_datas[i] != other._datas[i]) return false;
1060         }
1061         return true;
1062     }
1063 
1064     template<typename T>
1065     bool Matrix2D<T>::operator!=(const Matrix2D<T> &other) const {
1066         if (_row != other._row || _col != other._col) return true;
1067         for (size_t i = 0; i < _datas.size(); ++i) {
1068             if (_datas[i] != other._datas[i]) return true;
1069         }
1070         return false;
1071     }
