1078     template<typename T>
1079     T &Matrix2D<T>::operator()(uint32_t row, uint32_t col) {
1080         auto idx = row * _col + col;
1081         if (idx >= _datas.size()) throw std::out_of_range("[FATAL] The specified position is out of range!");
1082         return _datas[idx];
1083     }
