1085     template<typename T>
1086     void Matrix2D<T>::add(T &value, const std::function<void(T &, T &)> &function) {
1087         std::for_each(_datas.begin(), _datas.end(), [&function, &value](T &v) {
1088             if (function) {
1089                 function(v, value);
1090             } else if constexpr (std::is_integral_v<std::decay_t<T>> ||
1091                                  std::is_floating_point_v<std::decay_t<T>>) {
1092                 v += value;
1093             } else {
1094                 SDL_Log("[ERROR] Unsupported data type!\np.s: Did you forget to specify how to add values?");
1095             }
1096         });
1097     }
1098 
