1100     void Matrix2D<T>::add(T &&value, const std::function<void(T &, T &)> &function) {
1101         std::for_each(_datas.begin(), _datas.end(), [&function, &value](T &v) {
1102             if (function) {
1103                 function(v, value);
1104             } else if constexpr (std::is_integral_v<std::decay_t<T>> ||
1105                                  std::is_floating_point_v<std::decay_t<T>>) {
1106                 v += value;
1107             } else {
1108                 SDL_Log("[ERROR] Unsupported data type!\n"
1109                         "p.s: Did you forget to specify how to add values?");
1110             }
1111         });
1112     }
