1144     template<typename T>
1145     void Matrix2D<T>::times(T &value, const std::function<void(T &, T &)> &function) {
1146         std::for_each(_datas.begin(), _datas.end(), [&function, &value](T &v) {
1147             if (function) {
1148                 function(v, value);
1149             } else if constexpr (std::is_integral_v<std::decay_t<T>> ||
1150                                  std::is_floating_point_v<std::decay_t<T>>) {
1151                 v *= value;
1152             } else {
1153                 SDL_Log("[ERROR] Unsupported data type!\n"
1154                         "p.s: Did you forget to specify how to times values?");
1155             }
1156         });
