1159     template<typename T>
1160     void Matrix2D<T>::times(T &&value, const std::function<void(T &, T &)> &function) {
1161         std::for_each(_datas.begin(), _datas.end(), [&function, &value](T &v) {
1162             if (function) {
1163                 function(v, value);
1164             } else if constexpr (std::is_integral_v<std::decay_t<T>> ||
1165                                  std::is_floating_point_v<std::decay_t<T>>) {
1166                 v *= value;
