1174     template<typename T>
1175     void Matrix2D<T>::times(const Matrix2D<T> &other, const std::function<void(T &, const T &)> &function) {
1176         if ((_row == other._row) && (_col == other._col)) {
1177             if (function) {
1178                 for (size_t i = 0; i < _datas.size(); ++i) {
1179                     function(_datas[i], other._datas[i]);
1180                 }
1181             } else if constexpr (std::is_integral_v<std::decay_t<T>> ||
1182                                  std::is_floating_point_v<std::decay_t<T>>) {
1183                 for (size_t i = 0; i < _datas.size(); ++i) {
1184                     _datas[i] *= other._datas[i];
1185                 }
1186             } else {
1187                 SDL_Log("[ERROR] Unsupported data type!\n"
1188                         "p.s: Did you forget to specify how to times values?");
1189             }
1190         } else {
1191             SDL_Log("[ERROR] Matrix dimensions mismatch!\nOriginal: (%u, %u), Specified: (%u, %u)",
1192                     _row, _col, other._row, other._col);
1193         }
1194     }
1195 
1196     template<typename T>
1197     void Matrix2D<T>::multiply(const Matrix2D<T> &other) {
1198         if (_col != other._row) {
1199             SDL_Log("[ERROR] Matrix dimensions incompatible for multiplication!");
1200             return;
1201         }
1202         std::vector<T> result(_row * other._col);
1203         for (size_t i = 0; i < _row; ++i) {
1204             for (size_t k = 0; k < _col; ++k) {
1205                 const T &a_ik = _datas[i * _col + k];
1206                 for (size_t j = 0; j < other._col; ++j) {
1207                     result[i * other._col + j] += a_ik * other._datas[k * other._col + j];
1208                 }
1209             }
1210         }
1211         _datas = std::move(result);
1212         _col = other._col;
1213     }
1214 
1215     template<typename T>
1216     void Matrix2D<T>::transpose() {
1217         if (!_row || !_col) return;
1218         if (_row == _col) {
1219             for (size_t i = 0; i < _row; ++i) {
1220                 for (size_t j = i + 1; j < _col; ++j) {
1221                     std::swap(_datas[i * _col + j], _datas[j * _col + i]);
1222                 }
1223             }
1224             return;
1225         }
1226         std::vector<T> temp_vec;
1227         temp_vec.reserve(_row * _col);
1228         for (size_t j = 0; j < _col; ++j) {
1229             for (size_t i = 0; i < _row; ++i) {
1230                 temp_vec.push_back(_datas[i * _col + j]);
1231             }
1232         }
1233         std::swap(_datas, temp_vec);
1234         std::swap(_row, _col);
1235     }
1236 
1237     template<typename T>
1238     void Matrix2D<T>::reverse(bool reverse_row, bool reverse_col) {
1239         if (reverse_row && reverse_col) {
1240             const size_t M_SIZE = _datas.size() / 2;
1241             size_t _idx = 0, _last_idx = _datas.size() - 1;
1242             for (size_t i = 0; i < M_SIZE; ++i) {
1243                 std::swap(_datas[_idx++], _datas[_last_idx--]);
1244             }
1245         } else if (reverse_col) {
1246             const size_t M_SIZE_IN_COL = _col / 2;
1247             for (size_t i = 0; i < _row; ++i) {
1248                 for (size_t j = 0; j < M_SIZE_IN_COL; ++j) {
1249                     std::swap(_datas[i * _col + j], _datas[i * _col + _col - j - 1]);
1250                 }
1251             }
1252         } else if (reverse_row) {
1253             const size_t M_SIZE_IN_LINE = _row / 2;
1254             for (size_t i = 0; i < _col; ++i) {
1255                 for (size_t j = 0; j < M_SIZE_IN_LINE; ++j) {
1256                     std::swap(_datas[j * _col + i], _datas[(_row - j - 1) * _col + i]);
1257                 }
1258             }
1259         }
1260     }
1261 
1262     template<typename T>
1263     void Matrix2D<T>::rotate(bool turn_right) {
1264         if (!_row || !_col) return;
1265         std::vector<T> temp;
1266         temp.reserve(_row * _col);
1267         if (turn_right) {
1268             for (size_t c = 0; c < _col; ++c) {
1269                 for (size_t r = 0; r < _row; ++r) {
1270                     temp.emplace_back(_datas[(_row - r - 1) * _col + c]);
1271                 }
1272             }
1273         } else {
1274             for (size_t c = 0; c < _col; ++c) {
1275                 for (size_t r = 0; r < _row; ++r) {
1276                     temp.emplace_back(_datas[r * _col + (_col - c - 1)]);
1277                 }
1278             }
1279         }
1280         std::swap(temp, _datas);
1281         if (_row != _col) std::swap(_row, _col);
1282     }
1283 
1284     template<typename T>
1285     Matrix2D<T> Matrix2D<T>::splitRows(uint32_t start_row, uint32_t end_row) {
1286         if (start_row == end_row) {
1287             SDL_Log("[ERROR] The specified start row and end row cannot be the same!");
1288             return Matrix2D<T>();
1289         }
1290         if (end_row > _row) {
1291             SDL_Log("[ERROR] The specified end row exceeds the total rows of the original matrix!");
1292             return Matrix2D<T>();
1293         }
1294         if (start_row > end_row) std::swap(start_row, end_row);
1295         auto new_rows = end_row - start_row;
1296         Matrix2D<T> _ret(new_rows, _col);
1297         for (size_t i = 0; i < new_rows; ++i) {
1298             for (size_t j = 0; j < _col; ++j) {
1299                 _ret[i * _col + j] = _datas[(start_row + i) * _col + j];
1300             }
1301         }
1302         return _ret;
1303     }
1304 
1305     template<typename T>
1306     Matrix2D<T> Matrix2D<T>::splitCols(uint32_t start_col, uint32_t end_col) {
1307         if (start_col == end_col) {
1308             SDL_Log("[ERROR] The specified start col and end col cannot be the same!");
1309             return Matrix2D<T>();
1310         }
1311         if (end_col > _col) {
1312             SDL_Log("[ERROR] The specified end col exceeds the total cols of the original matrix!");
1313             return Matrix2D<T>();
1314         }
1315         if (start_col > end_col) std::swap(start_col, end_col);
1316         auto new_cols = end_col - start_col;
1317         Matrix2D<T> _ret(_row, new_cols);
1318         for (size_t i = 0; i < _row; ++i) {
1319             for (size_t j = 0; j < new_cols; ++j) {
1320                 _ret[i * new_cols + j] = _datas[i * _col + j + start_col];
1321             }
1322         }
1323         return _ret;
1324     }
1325 
1326     template<typename T>
1327     Matrix2D<T> Matrix2D<T>::split(uint32_t rows, uint32_t cols, const Matrix2D::Position &start_pos) {
1328         if (start_pos.row >= _row || start_pos.col >= _col) {
1329             SDL_Log("[ERROR] The specified start position is not valid!");
1330             return Matrix2D();
1331         }
1332         Matrix2D<T> _ret(rows, cols);
1333         auto size = rows * cols;
1334         auto start_index = start_pos.row * _col + start_pos.col;
1335         auto elapsed = _datas.size() - start_index;
1336         bool is_less = elapsed < size;
1337         if (!is_less) {
1338             for (size_t i = 0; i < _ret._datas.size(); ++i) {
1339                 _ret[i] = _datas[start_index++];
1340             }
1341         } else {
1342             for (size_t i = 0; i < elapsed; ++i) {
1343                 _ret[i] = _datas[start_index++];
1344             }
1345             for (size_t i = elapsed; i < _ret._datas.size(); ++i) {
1346                 _ret[i] = {};
1347             }
1348         }
1349         return _ret;
1350     }
1351 
1352     template<typename T>
1353     Matrix2D<T> Matrix2D<T>::split(Matrix2D::Position start_pos,
1354                                    Matrix2D::Position end_pos) {
1355         if (start_pos > end_pos) {
1356             auto temp = start_pos;
1357             start_pos = end_pos;
1358             end_pos = temp;
1359         }
1360         if (end_pos.row >= _row) {
1361             end_pos.row = _row - 1;
1362         }
1363         if (end_pos.col >= _col) {
1364             end_pos.col = _col - 1;
1365         }
1366         auto rows = end_pos.row - start_pos.row + 1;
1367         auto cols = end_pos.col - start_pos.col + 1;
1368         Matrix2D<T> _ret(rows, cols);
1369         for (size_t i = 0; i < rows; ++i) {
1370             for (size_t j = 0; j < cols; ++j) {
1371                 _ret[i * _ret._col + j] = _datas[(start_pos.row + i) * _col + j + start_pos.col];
1372             }
1373         }
1374         return _ret;
1375     }
1376 
1377     template<typename T>
1378     Matrix2D<T> Matrix2D<T>::inverse() {
1379         if (_row != _col) {
1380             SDL_Log("[ERROR] The specified matrix size is not matched!");
1381             return Matrix2D<T>();
1382         }
1383         if constexpr (!std::is_integral_v<std::decay_t<T>> &&
1384                       !std::is_floating_point_v<std::decay_t<T>>) {
1385             static_assert(!std::is_integral_v<std::decay_t<T>> &&
1386                           !std::is_floating_point_v<std::decay_t<T>>,
1387                           "[FATAL] Can't support the current data type!");
1388         }
1389         const uint32_t n = _row;
1390         Matrix2D<T> _ret(n, n);
1391         // 创建增广矩阵 [A|I]
1392         Matrix2D<T> augmented(n, 2 * n);
1393         // 填充增广矩阵：左侧为原矩阵，右侧为单位矩阵
1394         for (uint32_t i = 0; i < n; ++i) {
1395             for (uint32_t j = 0; j < n; ++j) {
1396                 augmented(i, j) = _datas[i * _col + j];  // 原矩阵
1397                 augmented(i, j + n) = (i == j) ? static_cast<T>(1) : static_cast<T>(0);  // 单位矩阵
1398             }
1399         }
1400         // 使用高斯-约旦消元法
1401         for (uint32_t i = 0; i < n; ++i) {
1402             // 寻找主元（部分主元选择）
1403             uint32_t max_row = i;
1404             for (uint32_t k = i + 1; k < n; ++k) {
1405                 if (std::abs(augmented(k, i)) > std::abs(augmented(max_row, i))) {
1406                     max_row = k;
1407                 }
1408             }
1409             // 如果主元为0，矩阵不可逆
1410             if (std::abs(augmented(max_row, i)) < static_cast<T>(1e-10)) {
1411                 SDL_Log("[ERROR] Matrix is singular and cannot be inverted!");
1412                 return Matrix2D<T>();
1413             }
1414             // 交换行
1415             if (max_row != i) {
1416                 for (uint32_t j = 0; j < 2 * n; ++j) {
1417                     std::swap(augmented(i, j), augmented(max_row, j));
1418                 }
1419             }
1420             // 将主元行标准化
1421             T pivot = augmented(i, i);
1422             for (uint32_t j = 0; j < 2 * n; ++j) {
1423                 augmented(i, j) /= pivot;
1424             }
1425             // 消去其他行的当前列
1426             for (uint32_t k = 0; k < n; ++k) {
1427                 if (k != i && std::abs(augmented(k, i)) > static_cast<T>(1e-10)) {
1428                     T factor = augmented(k, i);
1429                     for (uint32_t j = 0; j < 2 * n; ++j) {
1430                         augmented(k, j) -= factor * augmented(i, j);
1431                     }
1432                 }
1433             }
1434         }
1435         // 提取逆矩阵（增广矩阵的右侧部分）
1436         for (uint32_t i = 0; i < n; ++i) {
1437             for (uint32_t j = 0; j < n; ++j) {
1438                 _ret(i, j) = augmented(i, j + n);
1439             }
1440         }
1441         return _ret;
1442     }
1443 }
