1454     namespace Graphics {
1455         class Point {
1456         private:
1457             void update() {
1458                 S3GF::Algorithm::calcPoint(_position, _size / 2.f,
1459                                            _color, _vertices, _indices, _count);
1460             }
1461             Vector2 _position;
1462             uint16_t _size;
1463             SDL_Color _color;
1464             std::vector<SDL_Vertex> _vertices;
1465             std::vector<int> _indices;
1466             uint16_t _count{32};
1467         public:
1468             explicit Point() : _position(0, 0), _size(1), _color(StdColor::Black), _count(32) { update(); }
1469             Point(float x, float y, uint16_t size = 1, const SDL_Color& color = StdColor::Black,
1470                   uint16_t count = 32)
1471                 : _position(x, y), _size(size), _color(color), _count(count) { update(); }
1472             void move(float x, float y) { _position.reset(x, y); update(); }
1473             void move(const Vector2& new_pos) { _position.reset(new_pos); update(); }
1474             void resize(uint16_t new_size) { _size = new_size; update(); }
1475             void setColor(const SDL_Color& color) { _color = color; update(); }
1476             void setSegment(uint16_t segment = 32) { _count = segment; }
1477             void reset(const Vector2& pos, uint16_t size, const SDL_Color& color, uint16_t segment = 32) {
1478                 _position.reset(pos);
1479                 _size = size;
1480                 _color = color;
1481                 _count = segment;
1482                 update();
1483             }
1484             void reset(float x, float y, uint16_t size, const SDL_Color& color, uint16_t segment = 32) {
1485                 _position.reset(x, y);
1486                 _size = size;
1487                 _color = color;
1488                 _count = segment;
1489                 update();
1490             }
1491             const Vector2& position() const { return _position; }
1492             uint16_t size() const { return _size; }
1493             const SDL_Color& color() const { return _color; }
1494             const SDL_Vertex* vertices() const { return _vertices.data(); }
1495             const int* indices() const { return _indices.data(); }
1496             size_t verticesCount() const { return _vertices.size(); }
1497             size_t indicesCount() const { return _indices.size(); }
1498         };
