1500         class Line {
1501         public:
1502             explicit Line() : _start_position(), _end_position(), _size(1), _color(StdColor::Black) {}
1503             explicit Line(float x1, float y1, float x2, float y2, uint16_t size, const SDL_Color& color)
1504                 : _start_position(x1, y1), _end_position(x2, y2), _size(size), _color(color) {update();}
1505             explicit Line(const Vector2& start, const Vector2& end, uint16_t size, const SDL_Color &color)
1506                 : _start_position(start), _end_position(end), _size(size), _color(color) {update();}
1507 
1508             const int *indices() { return _indices.data(); }
1509             const SDL_Vertex *vertices() { return _vertices.data(); }
1510             size_t indicesCount() const { return _indices.size(); }
1511             size_t vertexCount() const { return _vertices.size(); }
1512             const Vector2& startPosition() const { return _start_position; }
1513             const Vector2& endPosition() const { return _end_position; }
1514             void setStartPosition(const Vector2& pos) { _start_position.reset(pos.x, pos.y); update(); }
1515             void setStartPosition(float x, float y) { _start_position.reset(x, y); update(); }
1516             void setEndPosition(const Vector2& pos) { _end_position.reset(pos.x, pos.y); update(); }
1517             void setEndPosition(float x, float y) { _end_position.reset(x, y); update(); }
1518             uint8_t size() const { return _size; }
1519             void setSize(uint8_t new_size) { _size = new_size; update(); }
1520             const SDL_Color& color() const { return _color; }
1521             void setColor(const SDL_Color& color) { _color = color; update(); }
1522             void reset(float sx, float sy, float ex, float ey, uint8_t size, const SDL_Color &color) {
1523                 _start_position.reset(sx, sy);
1524                 _end_position.reset(ex, ey);
1525                 _size = size;
1526                 _color = color;
1527                 update();
1528             }
1529             void reset(const Vector2& start, const Vector2& end, uint8_t size, const SDL_Color& color) {
1530                 _start_position.reset(start);
1531                 _end_position.reset(end);
1532                 _size = size;
1533                 _color = color;
1534                 update();
1535             }
1536         private:
1537             void update() {
1538                 S3GF::Algorithm::calcLine(_start_position.x, _start_position.y,
1539                                           _end_position.x, _end_position.y,
1540                                           _size, _color, _vertices, _indices);
1541             }
1542             Vector2 _start_position;
1543             Vector2 _end_position;
1544             uint8_t _size;
1545             SDL_Color _color;
1546             std::array<int, 6> _indices;
1547             std::array<SDL_Vertex, 4> _vertices;
1548         };
1549 
1550         class Rectangle {
1551         private:
1552             GeometryF _geometry;
1553             uint16_t _border_size;
1554             SDL_Color _border_color;
1555             SDL_Color _background_color;
1556             std::array<SDL_FRect, 4> _borders;
1557 
1558         public:
1559             explicit Rectangle() : _geometry(0, 0, 0, 0), _border_size(0),
1560                 _border_color(StdColor::Transparent), _background_color(StdColor::Transparent) {}
1561             explicit Rectangle(float x, float y, float w, float h,
1562                                uint16_t border = 1, const SDL_Color& border_color = StdColor::Black,
1563                                const SDL_Color& background_color = StdColor::Transparent)
1564                                : _geometry(x, y, w, h), _border_size(border),
1565                                  _border_color(border_color), _background_color(background_color) {
1566                 S3GF::Algorithm::calcRectangleBorder(_geometry, _border_size, _borders);
1567             }
1568             void reset(const GeometryF& geometry, uint16_t border,
1569                        const SDL_Color& border_color, const SDL_Color& background_color) {
1570                 _geometry.reset(geometry);
1571                 _border_size = border;
1572                 _border_color = border_color;
1573                 _background_color = background_color;
1574                 Algorithm::calcRectangleBorder(_geometry, _border_size, _borders);
1575             }
1576             void reset(const Vector2& pos, const Size& size, uint16_t border,
1577                        const SDL_Color& border_color, const SDL_Color& background_color) {
1578                 _geometry.reset(pos, size);
1579                 _border_size = border;
1580                 _border_color = border_color;
1581                 _background_color = background_color;
1582                 S3GF::Algorithm::calcRectangleBorder(_geometry, _border_size, _borders);
1583             }
1584             void reset(float x, float y, float w, float h, uint16_t border,
1585                        const SDL_Color& border_color, const SDL_Color& background_color) {
1586                 _geometry.reset(x, y, w, h);
1587                 _border_size = border;
1588                 _border_color = border_color;
1589                 _background_color = background_color;
1590                 S3GF::Algorithm::calcRectangleBorder(_geometry, _border_size, _borders);
1591             }
1592             void setGeometry(float x, float y, float w, float h) {
1593                 _geometry.reset(x, y, w, h);
1594                 S3GF::Algorithm::calcRectangleBorder(_geometry, _border_size, _borders);
1595             }
1596             void setGeometry(const Vector2& pos, const Size& size) {
1597                 _geometry.reset(pos, size);
1598                 S3GF::Algorithm::calcRectangleBorder(_geometry, _border_size, _borders);
1599             }
1600             void setGeometry(const GeometryF& geometry) {
1601                 _geometry = geometry;
1602                 S3GF::Algorithm::calcRectangleBorder(_geometry, _border_size, _borders);
1603             }
1604             void move(const Vector2& pos) {
1605                 _geometry.pos.reset(pos);
1606                 S3GF::Algorithm::calcRectangleBorder(_geometry, _border_size, _borders);
1607             }
1608             void move(float x, float y) {
1609                 _geometry.pos.reset(x, y);
1610                 S3GF::Algorithm::calcRectangleBorder(_geometry, _border_size, _borders);
1611             }
1612             void resize(const Size& size) {
1613                 _geometry.size.reset(size);
1614                 S3GF::Algorithm::calcRectangleBorder(_geometry, _border_size, _borders);
1615             }
1616             void resize(float w, float h) {
1617                 _geometry.size.reset(w, h);
1618                 S3GF::Algorithm::calcRectangleBorder(_geometry, _border_size, _borders);
1619             }
1620 
1621             void setBorderSize(uint16_t border_size) {
1622                 _border_size = border_size;
1623                 S3GF::Algorithm::calcRectangleBorder(_geometry, _border_size, _borders);
1624             }
1625             void setBorderColor(const SDL_Color& color) {
1626                 _border_color = color;
1627             }
1628             void setBackgroundColor(const SDL_Color& color) {
1629                 _background_color = color;
1630             }
1631             const GeometryF& geometry() const { return _geometry; }
1632             uint16_t borderSize() const { return _border_size; }
1633             const SDL_Color& borderColor() const { return _border_color; }
1634             const SDL_Color& backgroundColor() const { return _background_color; }
1635             const SDL_FRect* bordersRect() const { return _borders.data(); }
1636         };
1637 
1638         class Triangle {
1639         public:
1640             explicit Triangle() : _p1(0, 0), _p2(0, 0), _p3(0, 0),
1641                 _border_size(0), _border_color(StdColor::Transparent), _background_color(StdColor::Black) {}
1642             explicit Triangle(float x1, float y1, float x2, float y2, float x3, float y3,
1643                               uint16_t border_size = 0, const SDL_Color& border_color = StdColor::Transparent,
1644                               const SDL_Color& back_color = StdColor::Black)
1645                 : _p1(x1, y1), _p2(x2, y2), _p3(x3, y3),
1646                   _border_size(border_size), _border_color(border_color), _background_color(back_color)
1647                 { updateTri(); updateBorder(); }
1648 
1649             void reset(float x1, float y1, float x2, float y2, float x3, float y3,
1650                        uint16_t border_size, const SDL_Color& border_color, const SDL_Color& back_color) {
1651                 _p1.reset(x1, y1);
1652                 _p2.reset(x2, y2);
1653                 _p3.reset(x3, y3);
1654                 _border_size = border_size;
1655                 _border_color = border_color;
1656                 _background_color = back_color;
1657                 updateTri();
1658                 updateBorder();
1659             }
1660             void reset(const Vector2& pos1, const Vector2& pos2, const Vector2& pos3,
1661                        uint16_t border_size, const SDL_Color& border_color, const SDL_Color& back_color) {
1662                 _p1.reset(pos1);
1663                 _p2.reset(pos2);
1664                 _p3.reset(pos3);
1665                 _border_size = border_size;
1666                 _border_color = border_color;
1667                 _background_color = back_color;
1668                 updateTri();
1669                 updateBorder();
1670             }
1671             void setPosition(uint8_t index, const Vector2& pos) {
1672                 switch (index % 3) {
1673                     case 0:
1674                         _p1.reset(pos);
1675                         break;
1676                     case 1:
1677                         _p2.reset(pos);
1678                         break;
1679                     case 2:
1680                         _p3.reset(pos);
1681                         break;
1682                 }
1683                 updateTri();
1684                 updateBorder();
1685             }
1686             void setBorder(uint16_t border_size, const SDL_Color& color) {
1687                 _border_size = border_size;
1688                 _border_color = color;
1689                 updateBorder();
1690             }
1691             void setBackgroundColor(const SDL_Color& color) {
1692                 _background_color = color;
1693                 updateTri();
1694             }
1695             const Vector2& position(uint8_t index = 0) const {
1696                 switch (index % 3) {
1697                     case 0:
1698                         return _p1;
1699                     case 1:
1700                         return _p2;
1701                     case 2:
1702                         return _p3;
1703                 }
1704                 return _p1;
1705             }
1706             uint16_t borderSize() const { return _border_size; }
1707             const SDL_Color& borderColor() const { return _border_color; }
1708             const SDL_Color& backgroundColor() const { return _background_color; }
1709             const int *indices() { return _indices.data(); }
1710             const SDL_Vertex *vertices() { return _vertices.data(); }
1711             const int *borderIndices1() { return _bdi1.data(); }
1712             const int *borderIndices2() { return _bdi2.data(); }
1713             const int *borderIndices3() { return _bdi3.data(); }
1714             const SDL_Vertex *borderVertices1() { return _bd1.data(); }
1715             const SDL_Vertex *borderVertices2() { return _bd2.data(); }
1716             const SDL_Vertex *borderVertices3() { return _bd3.data(); }
1717             size_t borderIndicesCount() const { return _bdi1.size(); }
1718             size_t borderVerticesCount() const { return _bd1.size(); }
1719             size_t indicesCount() const { return _indices.size(); }
1720             size_t vertexCount() const { return _vertices.size(); }
1721         private:
1722             void updateTri() {
1723                 S3GF::Algorithm::calcTriangle(_p1, _p2, _p3, _background_color, _vertices, _indices);
1724             }
1725             void updateBorder() {
1726                 if (_border_size == 0) return;
1727                 S3GF::Algorithm::calcLine(_p1.x, _p1.y, _p2.x, _p2.y, _border_size, _border_color,_bd1, _bdi1);
1728                 S3GF::Algorithm::calcLine(_p2.x, _p2.y, _p3.x, _p3.y, _border_size, _border_color,_bd2, _bdi2);
1729                 S3GF::Algorithm::calcLine(_p1.x, _p1.y, _p3.x, _p3.y, _border_size, _border_color,_bd3, _bdi3);
1730             }
1731             Vector2 _p1;
1732             Vector2 _p2;
1733             Vector2 _p3;
1734             uint16_t _border_size;
1735             SDL_Color _border_color;
1736             SDL_Color _background_color;
1737             std::array<SDL_Vertex, 3> _vertices;
1738             std::array<int, 3> _indices;
1739             std::array<SDL_Vertex, 4> _bd1, _bd2, _bd3;
1740             std::array<int, 6> _bdi1, _bdi2, _bdi3;
1741         };
1742 
1743         class Ellipse {
1744         public:
1745             explicit Ellipse() : _center_point(0, 0), _radius(0, 0),
1746                 _border_size(0), _border_color(StdColor::Transparent), _background_color(StdColor::Transparent),
1747                 _degree(0.f), _count(0) {}
1748 
1749             Ellipse(float cx, float cy, float rw, float rh, uint16_t border_size = 1,
1750                     const SDL_Color& border_color = StdColor::Black,
1751                     const SDL_Color& back_color = StdColor::Transparent,
1752                     float degree = 0.f, uint16_t segment = 32)
1753                     : _center_point(cx, cy), _radius(rw, rh), _border_size(border_size),
1754                       _border_color(border_color), _background_color(back_color),
1755                       _degree(degree), _count(segment) {
1756                 S3GF::Algorithm::calcEllipse(_center_point, _radius,
1757                                              _background_color, _degree, _count,
1758                                              _vertices, _indices);
1759                 S3GF::Algorithm::calcEllipseRing(_center_point, _radius, _border_size,
1760                                                  _border_color, _degree, _count,
1761                                                  _border_vertices, _border_indices);
1762             }
1763 
1764             void reset(float cx, float cy, float rw, float rh, uint16_t border_size,
1765                        const SDL_Color& border_color, const SDL_Color& back_color, float degree,
1766                        uint16_t segment = 32) {
1767                 _center_point.reset(cx, cy);
1768                 _radius.reset(rw, rh);
1769                 _border_size = border_size;
1770                 _border_color = border_color;
1771                 _background_color = back_color;
1772                 _degree = degree;
1773                 _count = segment;
1774                 S3GF::Algorithm::calcEllipse(_center_point, _radius,
1775                                              _background_color, _degree, _count,
1776                                              _vertices, _indices);
1777                 S3GF::Algorithm::calcEllipseRing(_center_point, _radius, _border_size,
1778                                                  _border_color, _degree, _count,
1779                                                  _border_vertices, _border_indices);
1780             }
1781 
1782             void reset(const Vector2& center_pos, const Size& radius, uint16_t border_size,
1783                        const SDL_Color& border_color, const SDL_Color& back_color, float degree,
1784                        uint16_t segment = 32) {
1785                 _center_point.reset(center_pos);
1786                 _radius.reset(radius);
1787                 _border_size = border_size;
1788                 _border_color = border_color;
1789                 _background_color = back_color;
1790                 _degree = degree;
1791                 _count = segment;
1792                 S3GF::Algorithm::calcEllipse(_center_point, _radius,
1793                                              _background_color, _degree, _count,
1794                                              _vertices, _indices);
1795                 S3GF::Algorithm::calcEllipseRing(_center_point, _radius, _border_size,
1796                                                  _border_color, _degree, _count,
1797                                                  _border_vertices, _border_indices);
1798             }
1799 
1800             void setGeometry(float x, float y, float rw, float rh) {
1801                 _center_point.reset(x, y);
1802                 _radius.reset(rw, rh);
1803                 S3GF::Algorithm::calcEllipse(_center_point, _radius,
1804                                              _background_color, _degree, _count,
1805                                              _vertices, _indices);
1806                 S3GF::Algorithm::calcEllipseRing(_center_point, _radius, _border_size,
1807                                                  _border_color, _degree, _count,
1808                                                  _border_vertices, _border_indices);
1809             }
1810 
1811             void setGeometry(const Vector2& position, const Size& size) {
1812                 _center_point.reset(position);
1813                 _radius.reset(size);
1814                 S3GF::Algorithm::calcEllipse(_center_point, _radius,
1815                                              _background_color, _degree, _count,
1816                                              _vertices, _indices);
1817                 S3GF::Algorithm::calcEllipseRing(_center_point, _radius, _border_size,
1818                                                  _border_color, _degree, _count,
1819                                                  _border_vertices, _border_indices);
1820             }
1821 
1822             void setBorder(uint16_t size, const SDL_Color& color) {
1823                 _border_size = size;
1824                 _border_color = color;
1825                 S3GF::Algorithm::calcEllipseRing(_center_point, _radius, _border_size,
1826                                                  _border_color, _degree, _count,
1827                                                  _border_vertices, _border_indices);
1828             }
1829 
1830             void setBackground(const SDL_Color& color) {
1831                 _background_color = color;
1832                 S3GF::Algorithm::calcEllipse(_center_point, _radius,
1833                                              _background_color, _degree, _count,
1834                                              _vertices, _indices);
1835             }
1836 
1837             void setRotate(float rotate) {
1838                 _degree = rotate;
1839                 S3GF::Algorithm::calcEllipse(_center_point, _radius,
1840                                              _background_color, _degree, _count,
1841                                              _vertices, _indices);
1842                 S3GF::Algorithm::calcEllipseRing(_center_point, _radius, _border_size,
1843                                                  _border_color, _degree, _count,
1844                                                  _border_vertices, _border_indices);
1845             }
1846 
1847             const Vector2& centerPosition() const { return _center_point; }
1848             const Size& radius() const { return _radius; }
1849             uint16_t borderSize() const { return _border_size; }
1850             const SDL_Color& borderColor() const { return _border_color; }
1851             const SDL_Color& backgroundColor() const { return _background_color; }
1852             float rotateDegree() const { return _degree; }
1853             const int *indices() const { return _indices.data(); }
1854             const SDL_Vertex *vertices() const { return _vertices.data(); }
1855             size_t indicesCount() const { return _indices.size(); }
1856             size_t vertexCount() const { return _vertices.size(); }
1857             const int *borderIndices() const { return _border_indices.data(); }
1858             const SDL_Vertex *borderVertices() const { return _border_vertices.data(); }
1859             size_t borderIndicesCount() const { return _border_indices.size(); }
1860             size_t borderVerticesCount() const { return _border_vertices.size(); }
1861 
1862         private:
1863             Vector2 _center_point;
1864             Size _radius;
1865             uint16_t _border_size;
1866             SDL_Color _border_color;
1867             SDL_Color _background_color;
1868             float _degree;
1869             uint16_t _count{32};
1870             std::vector<SDL_Vertex> _vertices, _border_vertices;
1871             std::vector<int> _indices, _border_indices;
1872         };
1873     }
