9     using GT = std::variant<std::monostate, Graphics::Point, Graphics::Rectangle>;
10     inline uint64_t _next_id = 0;
11 
12     class ClickArea {
13     public:
14         explicit ClickArea(uint64_t window_id, GT graphic)
15             : _base(std::move(graphic)), _winID(window_id) {
16             EventSystem::global()->appendEvent(_next_id++, [this](SDL_Event ev) {
17                 if (ev.window.windowID != _winID) {
18                     _is_pressed = false;
19                     _is_hovered = false;
20                     return;
21                 }
22                 if (!_enabled) return;
23                 Vector2 cur_pos = Cursor::global()->position();
24                 int8_t tri = -1;
25                 switch (_base.index()) {
26                     case 1:
27                         tri = Algorithm::comparePosInPoint(cur_pos, std::get<Graphics::Point>(_base));
28                         break;
29                     case 2:
30                         tri = Algorithm::comparePosInRect(cur_pos, std::get<Graphics::Rectangle>(_base));
31                         break;
32                     default:
33                         break;
34                 }
35                 if (tri > 0) {
36                     if (!_is_hovered) {
37                         _is_hovered = true;
38                     }
39                     if (ev.button.type == SDL_EVENT_MOUSE_BUTTON_DOWN || ev.key.type == SDL_EVENT_KEY_DOWN) {
40                         _is_down = true;
41                     } else if (ev.button.type == SDL_EVENT_MOUSE_BUTTON_UP || ev.key.type == SDL_EVENT_KEY_UP) {
42                         _is_down = false;
43                         _is_pressed = true;
44                     }
45                 } else {
46                     _is_hovered = false;
47                     _is_pressed = false;
48                     _is_entered = false;
49                 }
50                 if (_is_hovered && !_is_entered) _is_entered = true;
51                 if (_is_pressed && _func) {
52                     _func();
53                     _is_pressed = false;
54                 }
55             });
56         }
57 
58         void setEnabled(bool enabled) {
59             _enabled = enabled;
60         }
61 
62         bool enabled() {
63             return _enabled;
64         }
65 
66         void setPressedEvent(std::function<void()> function) {
67             _func = std::move(function);
68         }
69 
70         void resetPress() { _is_pressed = false; }
71         void setHover(bool enabled) { _is_hovered = enabled; }
72 
73         void setGraphic(GT graphic) {
74             _base = std::move(graphic);
75 
76         }
77 
78         bool setPoint(const Vector2& position, uint16_t size) {
79             if (_base.index() != 1) return false;
80             auto& pt = std::get<Graphics::Point>(_base);
81             pt.reset(position, size, pt.color());
82             return true;
83         }
84 
85         bool setRect(const GeometryF& geometry) {
86             if (_base.index() != 2) return false;
87             auto& rect = std::get<Graphics::Rectangle>(_base);
88             rect.setGeometry(geometry);
89             return true;
90         }
91 
92         Graphics::Point& point() {
93             if (_base.index() != 1) {
94                 throw std::runtime_error("ClickArea: The graphic is not point!");
95             }
96             return std::get<Graphics::Point>(_base);
97         }
98 
99         Graphics::Rectangle& rectangle() {
100             if (_base.index() != 2) {
101                 throw std::runtime_error("ClickArea: The graphic is not rectangle!");
102             }
103             return std::get<Graphics::Rectangle>(_base);
104         }
105 
106         bool getPoint(Graphics::Point& point) {
107             if (_base.index() != 1) return false;
108             point = std::get<Graphics::Point>(_base);
109             return true;
110         }
111 
112         bool getRect(Graphics::Rectangle& rect) {
113             if (_base.index() != 2) return false;
114             auto o_rect = std::get<Graphics::Rectangle>(_base);
115             rect.reset(o_rect.geometry(), o_rect.borderSize(), o_rect.borderColor(), o_rect.backgroundColor());
116             return true;
117         }
118 
119         [[nodiscard]] bool isPressed() const { return _is_pressed; }
120         [[nodiscard]] bool isHovered() const { return _is_hovered; }
121         [[nodiscard]] bool isEntered() const { return _is_entered; }
122         [[nodiscard]] bool isDown() const { return _is_down; }
123         [[nodiscard]] size_t index() const { return _base.index(); }
124     private:
125         GT _base;
126         uint64_t _winID;
127         bool _is_pressed{false}, _is_hovered{false}, _is_entered{false}, _is_down{false}, _is_triggered{false};
128         bool _enabled{true};
129         std::function<void()> _func;
130     };
131 
132     class AbstractControl {
133     public:
134         enum State {
135             Normal,
136             Hover,
137             Pressed,
138             Disabled,
139             Selected
140         };
141 
142         explicit AbstractControl(const std::string& name, Renderer* renderer,
143                                  GT click_area = Graphics::Rectangle(),
144                                  AbstractControl* parent = nullptr)
145             : _name(name), _renderer(renderer), _parent(parent), _id(++_next_id),
146               _click_area(renderer->window()->windowID(), std::move(click_area)) {
147 
148             renderer->window()->installPaintEvent([this] (Renderer* r) {
149                 if (!_visible) return;
150                 paintEvent(r);
151             });
152 
153             EventSystem::global()->appendEvent(_next_id++, [this](SDL_Event ev) {
154                 if (!_visible || !_enabled) return;
155 
156                 bool _entered_changed = (_entered != _click_area.isEntered());
157                 if (_entered_changed) {
158                     _entered = _click_area.isEntered();
159                     if (_entered) onEntered(); else onLeft();
160                 }
161                 if (_click_area.isHovered()) onHovered();
162                 bool _button_down_changed = (_is_down != _click_area.isDown());
163                 if (_button_down_changed) {
164                     _is_down = _click_area.isDown();
165                     if (_is_down) onPressed(); else onReleased();
166                 }
167                 if (_click_area.isPressed()) {
168                     onTriggered();
169                     _click_area.resetPress();
170                 }
171             });
172 
173             if (_click_area.index() == 1) {
174                 Graphics::Point& pt = _click_area.point();
175                 _geometry.resetPos(pt.position());
176                 auto size = static_cast<float>(pt.size());
177                 _geometry.resetSize({size, size});
178             } else if (_click_area.index() == 2) {
179                 Graphics::Rectangle& re = _click_area.rectangle();
180                 _geometry.reset(re.geometry());
181             }
182             loadEvent();
183         }
184 
185         ~AbstractControl() { unloadEvent(); };
186 
187         void setName(const std::string& name) {
188             _name = name;
189         }
190 
191         [[nodiscard]] const std::string& name() const {
192             return _name;
193         }
194 
195         void move(float x, float y) {
196             _geometry.resetPos({x, y});
197             _update_click_area();
198             moveEvent();
199         }
200 
201         void move(const Vector2& pos) {
202             _geometry.resetPos(pos);
203             _update_click_area();
204             moveEvent();
205         }
206 
207         void move(Vector2&& pos) {
208             _geometry.resetPos(pos);
209             _update_click_area();
210             moveEvent();
211         }
212 
213         void resize(float w, float h) {
214             _geometry.resetSize({w, h});
215             _update_click_area();
216             resizeEvent();
217         }
218 
219         void resize(const Size& size) {
220             _geometry.resetSize(size);
221             _update_click_area();
222             resizeEvent();
223         }
224 
225         void resize(Size&& size) {
226             _geometry.resetSize(size);
227             _update_click_area();
228             resizeEvent();
229         }
230 
231         void setGeometry(float x, float y, float w, float h) {
232             _geometry.reset(x, y, w, h);
233             _update_click_area();
234             moveEvent();
235             resizeEvent();
236         }
237 
238         void setGeometry(const Vector2& pos, const Size& size) {
239             _geometry.reset(pos, size);
240             _update_click_area();
241             moveEvent();
242             resizeEvent();
243         }
244 
245         void setGeometry(Vector2&& pos, Size&& size) {
246             _geometry.reset(pos, size);
247             _update_click_area();
248             moveEvent();
249             resizeEvent();
250         }
251 
252         void setGeometry(const GeometryF& geometry) {
253             _geometry.reset(geometry);
254             _update_click_area();
255             moveEvent();
256             resizeEvent();
257         }
258 
259         void setGeometry(GeometryF&& geometry) {
260             _geometry.reset(geometry);
261             _update_click_area();
262             moveEvent();
263             resizeEvent();
264         }
265 
266         [[nodiscard]] const GeometryF& geometry() const { return _geometry; }
267         [[nodiscard]] const Vector2& position() const { return _geometry.pos; }
268         [[nodiscard]] const Size& size() const { return _geometry.size; }
269 
270         void setVisible(bool visible) {
271             _visible = visible;
272             visibleChanged();
273         }
274         [[nodiscard]] bool visible() const { return _visible; }
275         void setEnabled(bool enabled) {
276             _enabled = enabled;
277             enabledChanged();
278         }
279         [[nodiscard]] bool enabled() const { return _enabled; }
280 
281     protected:
282         virtual void paintEvent(Renderer*) {}
283         virtual void loadEvent() {}
284         virtual void unloadEvent() {}
285         virtual void moveEvent() {}
286         virtual void resizeEvent() {}
287         virtual void onPressed() {}
288         virtual void onReleased() {}
289         virtual void onTriggered() {}
290         virtual void onHovered() {}
291         virtual void onEntered() {}
292         virtual void onLeft() {}
293         virtual void enabledChanged() {}
294         virtual void visibleChanged() {}
295 
296         void _update_click_area() {
297             if (_click_area.index() == 1) {
298                 auto min = std::min(_geometry.size.width, _geometry.size.height);
299                 auto real_size = std::min(static_cast<int>(min), UINT16_MAX);
300                 _click_area.setPoint(_geometry.pos, real_size);
301             } else if (_click_area.index() == 2) {
302                 _click_area.setRect(_geometry);
303                 Logger::log(std::format("Changed: {}, {} {}x{}",
304                         _geometry.pos.x, _geometry.pos.y, _geometry.size.width, _geometry.size.height));
305             }
306         }
307 
308     protected:
309         uint64_t _id{0};
310         std::string _name;
311         Renderer* _renderer;
312         AbstractControl* _parent;
313         GeometryF _geometry;
314         ClickArea _click_area;
315         bool _is_down{false};
316         bool _visible{true}, _enabled{true}, _entered{false};
317     };
