1 #pragma once
2 #ifndef S3GF_RANDOM_H
3 #define S3GF_RANDOM_H
4 #include "Libs.h"
5 
6 namespace S3GF {
7     class RandomGenerator {
8     public:
9         RandomGenerator() = delete;
10         RandomGenerator(RandomGenerator &&) = delete;
11         RandomGenerator(const RandomGenerator &) = delete;
12         RandomGenerator &operator=(RandomGenerator &&) = delete;
13         RandomGenerator &operator=(const RandomGenerator &) = delete;
14         ~RandomGenerator() = delete;
15 
16         static int randInt(int start, int end) {
17             std::uniform_int_distribution<int> uni(start, end);
18             if (!_is_init) _def_rand_eng.seed(time(0));
19             return uni(_def_rand_eng);
20         }
21 
22         static int64_t randBigInt(int64_t start, int64_t end) {
23             std::uniform_int_distribution<int64_t> uni(start, end);
24             if (!_is_init) {
25                 _def_rand_eng.seed(time(nullptr));
26                 _is_init = true;
27             }
28             return uni(_def_rand_eng);
29         }
30 
31         static uint32_t randUInt(uint32_t start, uint32_t end) {
32             std::uniform_int_distribution<uint32_t> uni(start, end);
33             if (!_is_init) {
34                 _def_rand_eng.seed(time(nullptr));
35                 _is_init = true;
36             }
37             return uni(_def_rand_eng);
38         }
39 
40         static uint64_t randBigUInt(uint64_t start, uint64_t end) {
41             std::uniform_int_distribution<uint64_t> uni(start, end);
42             if (!_is_init) {
43                 _def_rand_eng.seed(time(nullptr));
44                 _is_init = true;
45             }
46             return uni(_def_rand_eng);
47         }
48 
49         static float randFloat(float start, float end) {
50             std::uniform_real_distribution<float> uni(start, end);
51             if (!_is_init) {
52                 _def_rand_eng.seed(time(nullptr));
53                 _is_init = true;
54             }
55             return uni(_def_rand_eng);
56         }
57 
58         static double randDouble(double start, double end) {
59             std::uniform_real_distribution<double> uni(start, end);
60             if (!_is_init) {
61                 _def_rand_eng.seed(time(nullptr));
62                 _is_init = true;
63             }
64             return uni(_def_rand_eng);
65         }
66 
67         static long double randBigDouble(long double start, long double end) {
68             std::uniform_real_distribution<long double> uni(start, end);
69             if (!_is_init) {
70                 _def_rand_eng.seed(time(nullptr));
71                 _is_init = true;
72             }
73             return uni(_def_rand_eng);
74         }
75 
76         static std::random_device _rnd_dev;
77         static std::default_random_engine _def_rand_eng;
78         static bool _is_init;
79     };
80 
81     inline std::random_device RandomGenerator::_rnd_dev{};
82     inline std::default_random_engine RandomGenerator::_def_rand_eng{};
83     inline bool RandomGenerator::_is_init{false};
84 }
